package SlidingWindow;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class FindAllAnagramsinaString {

	public static void main(String[] args) {
		/**
		 * 給定兩組字串s,p
		 * 確認s中有哪些位置的子字串，是p的Anagramsina字串(易位構詞)
		 */
		String s = "cbaebabacd";
		String p = "abc";
		
		System.out.println(findAnagrams(s, p));
		
		s = "ababababab";
		p = "aab";
		
		System.out.println(findAnagrams(s, p));
		
		s = "";
		p = "";
		
		System.out.println(findAnagrams(s, p));
		
		s = "baa";
		p = "aa";
		
		System.out.println(findAnagrams(s, p));
	}

    public static List<Integer> findAnagrams(String s, String p) {
        /**
         * 若是一個一個位置都檢查將會超時
         * 採用sliding window
         */
    	List<Integer> results = new ArrayList<>();
    	Map<Character, Integer> charMap = new HashMap<>();
    	
    	/* 用一個map來紀錄char以及其個數 */
    	for (int i = 0; i < p.length(); i++) {
    		charMap.put(p.charAt(i), charMap.getOrDefault(p.charAt(i), 0)+1);
    	}
    	
    	int left = 0;
    	int right = p.length()-1;
    	/**
    	 * 先初始化位置0~p.length()-1的部分，若是有在map的char，就減去其數量
    	 */
    	for (int i = left; i <= right; i++) {
    		char curChar = s.charAt(i);
    		if (charMap.containsKey(curChar)) {
				charMap.put(curChar, charMap.get(curChar)-1);
			}
    	}
    	
    	while (right < s.length()) {
    		
    		if (isAllUsed(charMap)) { /* 用map中的所有char都被用完了，表示left這個位置符合條件 */
				results.add(left);
			}
    		
    		if (right == s.length()-1) {
				break;
			}
    		/**
    		 * 接著向後滑動window
    		 * 原先left的char會lose，所以map中加回來
    		 * 滑動後right的char是新增的，所以map中減去
    		 */
    		char charToLose = s.charAt(left);
    		left++;
    		right++;
    		char charToGain = s.charAt(right);
    		
    		if (charMap.containsKey(charToLose)) {
				charMap.put(charToLose, charMap.get(charToLose)+1);
			}
    		if (charMap.containsKey(charToGain)) {
				charMap.put(charToGain, charMap.get(charToGain)-1);
			}
    	}
    	
    	return results;
    }

	private static boolean isAllUsed(Map<Character, Integer> charMap) {
		
		for (Character ch : charMap.keySet()) {
			
			if (charMap.get(ch) != 0) {
				return false;
			}
		}
		
		return true;
	}
}
